	class FCall
	{
	public:
		FCall(){}
		virtual ~FCall(){}
		virtual bool Call(){return false;}
		virtual FCall* Clone(){return 0;}
	};
		
	class FCallPtr
	{
	public:
		FCallPtr() : m_pFCall(NULL){}
		FCallPtr(FCall* lpFCall) : m_pFCall(lpFCall) {}
		FCallPtr(FCallPtr& ref) : m_pFCall(NULL) { ATLASSERT(FALSE); }
		~FCallPtr(){ Delete(); }
		FCallPtr& operator=(FCallPtr& ref){	ATLASSERT(FALSE); }
		FCallPtr& operator=(FCall* lpFCall)
		{
			if(lpFCall != m_pFCall)
			{
				Delete();
				m_pFCall = lpFCall;
			}
			return *this;
		}
		operator FCall*()
		{
			return m_pFCall;
		}

		FCall& operator*()
		{
			return *m_pFCall;
		}

		FCall* operator->()
		{
			return m_pFCall;
		}

		FCall** operator &()
		{
			return &m_pFCall;
		}
				
	public:
		FCall* Attach(FCall* lpFCall)
		{
			ATLASSERT(NULL == m_pFCall);	
			FCall* pCall = Detach();
			m_pFCall = lpFCall;
			return pCall;
		}
		FCall* Detach()
		{
			FCall* pCall = m_pFCall; 
			m_pFCall = NULL; 
			return pCall;
		}
		void Delete()
		{
			if(m_pFCall)
			{
				delete m_pFCall;
				m_pFCall = NULL;
			}
		}
	protected:
		FCall* m_pFCall;
	};

